    VERIFY_IS_EQUAL(group_static.size(), 32u);
    VERIFY_IS_EQUAL(group_static.globalFlags(), 0);
    group_static.apply<checkIdx, int>(identity10, 0, found, expected);
    VERIFY_IS_EQUAL(found.size(), 32u);
